import React, { Component } from 'react'
import Nav from './Nav';

export default class Life extends Component {
    constructor(){
        super()
        this.state={
            show:true
        }
        console.warn('constructor');
    }
    componentDidMount(){
        console.warn('didMount')
    }
    
    
  render() {
    return (
      <div>
        <h1>life cycle</h1>
        {
            this.state.show ? 
            <Nav/>
            :null
        }
        <button onClick={()=>{this.SetState({show:!this.state.show})}}>toggle button</button>
      </div>
    )
  }
}
